.employment-page ol,
.employment-page ul {
    margin: 0;
    padding: 0;
    list-style: none
}

.employment-page h1,
.employment-page h2,
.employment-page h3,
.employment-page h4,
.employment-page h5,
.employment-page h6,
.employment-page p {
    margin: 0;
    padding: 0
}

.employment-page img {
    -webkit-user-drag: none;
    border: 0;
    max-width: 100%;
    height: auto
}

.employment-page .employment {
    max-width: 1196px;
    margin: 0 auto
}

.employment-page .employment__gradient {
    background-color: #f04d1133;
    background-image: linear-gradient(90deg, #ff5f6d, #ffca6f)
}

.employment-page .employment__section {
    margin-top: 56px
}

.employment-page .employment__header {
    font-variation-settings: "wght" 720;
    text-align: center;
    padding: 0 12px;
    font-size: 24px;
    font-weight: 400;
    line-height: 32px
}

.employment-page .employment__header--storage {
    margin-bottom: 32px
}

.employment-page .employment__header--driver {
    margin-bottom: 36px
}

.employment-page .employment__link {
    font-variation-settings: "wght" 720;
    text-align: center;
    border-radius: 12px;
    margin-bottom: 8px;
    padding: 10px 8px 14px;
    font-size: 14px;
    font-weight: 400;
    line-height: 20px;
    display: block
}

.employment-page .employment__link--yellow {
    color: #000;
    background: #f6f6fab3;
    transition: background .3s
}

.employment-page .employment__link--yellow:hover {
    background: #f6f6facc
}

.employment-page .employment__link--red {
    color: #fff;
    background-color: #f04d1133
}

.employment-page .intro {
    border-radius: 16px;
    padding: 16px 16px 0
}

.employment-page .intro__header {
    color: #fff;
    font-variation-settings: "wght" 720;
    width: 100%;
    margin-bottom: 24px;
    font-size: 24px;
    font-weight: 400;
    line-height: 32px
}

.employment-page .intro__link {
    width: 100%
}

.employment-page .intro__img {
    width: 100%;
    height: 0;
    padding-top: 64%;
    position: relative
}

.employment-page .intro__img:before {
    content: "";
    background-image: url(https://static-basket-01.wbbasket.ru/vol2/wbx-static/prod/assets/images/employment/intro.png);
    background-position: 50%;
    background-repeat: no-repeat;
    background-size: contain;
    width: 73%;
    max-width: 320px;
    padding-top: 78%;
    position: absolute;
    top: 22px;
    left: 50%;
    transform: translate(-50%)
}

.employment-page .drivers-steps__item {
    text-align: center;
    background-position: top;
    background-repeat: no-repeat;
    background-size: auto 62px;
    margin-bottom: 40px;
