}

.employment-page .drivers-steps__item.app {
    background-image: url(https://static-basket-01.wbbasket.ru/vol2/wbx-static/prod/assets/images/employment/app.svg)
}

.employment-page .drivers-steps__item.register {
    background-image: url(https://static-basket-01.wbbasket.ru/vol2/wbx-static/prod/assets/images/employment/register.svg)
}

.employment-page .drivers-steps__item.check {
